import React, { useContext, useReducer, useState } from 'react';
import { Helmet } from 'react-helmet-async';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Store } from '../Store';
import { toast } from 'react-toastify';
import { getError } from '../utils';
import axios from 'axios';
import { FormGroup } from 'react-bootstrap';

const reducer = (state, action) => {
  switch (action.type) {
    case 'UPDATE_REQUEST':
      return { ...state, loadingUpdate: true };
    case 'UPDATE_SUCCESS':
      return { ...state, loadingUpdate: false };
    case 'UPDATE_FAIL':
      return { ...state, loadingUpdate: false };
    default:
      return state;
  }
};

export default function ProfileScreen() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const [name, setName] = useState(userInfo.name);
  const [schoolYear, setSchoolYear] = useState(userInfo.schoolYear);
  const [email, setEmail] = useState(userInfo.email);
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [profilePic, setProfilePic] = useState(null);
  const [{ loadingUpdate }, dispatch] = useReducer(reducer, {
    loadingUpdate: false,
  });

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('schoolYear', schoolYear);
      formData.append('email', email);
      formData.append('password', password);
      if (profilePic) {
        formData.append('profilePic', profilePic);
      }

      const { data } = await axios.put('/api/users/profile', formData, {
        headers: { Authorization: `Bearer ${userInfo.token}` },
      });

      dispatch({
        type: 'UPDATE_SUCCESS',
      });

      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      toast.success('User updated successfully');
    } catch (err) {
      dispatch({
        type: 'FETCH_FAIL',
      });
      toast.error(getError(err));
    }
  };

  const handleProfilePicChange = (e) => {
    setProfilePic(e.target.files[0]);
  };

  return (
    <div className="container small-container">
  <Helmet>
    <title>User Profile</title>
  </Helmet>
  <h1 className="my-3">User Profile</h1>
  <form onSubmit={submitHandler}>
    <Form.Group className="mb-3" controlId="name">
      <Form.Label>Name</Form.Label>
      <Form.Control value={name} onChange={(e) => setName(e.target.value)} required />
    </Form.Group>
    <Form.Group className="mb-3" controlId="schoolYear">
      <Form.Label>School Year</Form.Label>
      <Form.Control value={schoolYear} onChange={(e) => setSchoolYear(e.target.value)} required />
    </Form.Group>
    <Form.Group className="mb-3" controlId="email">
      <Form.Label>Email</Form.Label>
      <Form.Control type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
    </Form.Group>
    <Form.Group className="mb-3" controlId="password">
      <Form.Label>Password</Form.Label>
      <Form.Control type="password" onChange={(e) => setPassword(e.target.value)} />
    </Form.Group>
    <FormGroup className="mb-3" controlId='profilePic'>
      <Form.Label>ProfilePicture</Form.Label>
      <Form.Control type="Picture" value={profilePic} onChange={(e) => setProfilePic(e.target.value)} />
    </FormGroup>
  </form>
</div>

  )}
