import React, { useState } from 'react';
import './uploadProductScreen.css';
import axios from 'axios';

const UploadProductScreen = () => {
  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productPrice, setProductPrice] = useState(0);
  const [productImage, setProductImage] = useState(null);

  const handleProductNameChange = (event) => {
    setProductName(event.target.value);
  };

  const handleProductDescriptionChange = (event) => {
    setProductDescription(event.target.value);
  };

  const handleProductPriceChange = (event) => {
    setProductPrice(event.target.value);
  };

  const handleProductImageChange = (event) => {
    setProductImage(event.target.files[0]);
  };

  const handleUploadProduct = async () => {
    // create FormData object to send image file
    const formData = new FormData();
    formData.append('picture', productImage);

    // add remaining product data to FormData object
    formData.append('title', productName);
    formData.append('description', productDescription);
    formData.append('price', productPrice);

    // make API call to upload product data to server
    try {
      const response = await axios.post('http://localhost:8080/api/inventory/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log('Product uploaded successfully:', response.data);
    } catch (error) {
      console.error('Error uploading product:', error.message);
    }
  };

  return (
    <div className="upload-product-screen">
      <h1>Upload Product</h1>
      <form>
        <label htmlFor="productName">Product Name:</label>
        <input
          type="text"
          id="productName"
          name="productName"
          value={productName}
          onChange={handleProductNameChange}
        />
        <br />
        <label htmlFor="productDescription">Product Description:</label>
        <textarea
          id="productDescription"
          name="productDescription"
          value={productDescription}
          onChange={handleProductDescriptionChange}
        ></textarea>
        <br />
        <label htmlFor="productPrice">Product Price:</label>
        <input
          type="number"
          id="productPrice"
          name="productPrice"
          value={productPrice}
          onChange={handleProductPriceChange}
        />
        <br />
        <label htmlFor="productImage">Product Image:</label>
        <input
          type="file"
          id="productImage"
          name="productImage"
          onChange={handleProductImageChange}
        />
        <br />
        <button
          type="button"
          className="yellow-button"
          onClick={handleUploadProduct}
        >
          Upload Product
        </button>
      </form>
    </div>
  );
};

export default UploadProductScreen;
